import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { HttpClient, HttpClientModule } from '@angular/common/http';
import { FormsModule } from '@angular/forms';

interface Score {
  id: number;
  user_name: string;
  score: number;
  posicion: number; // agregamos la propiedad posicion
}

@Component({
  selector: 'app-puntaje',
  standalone: true,
  imports: [CommonModule, HttpClientModule, FormsModule],
  templateUrl: './puntaje.component.html',
  styleUrls: ['./puntaje.component.scss']
})
export class PuntajeComponent {
  scores: Score[] = [];
  filteredScores: Score[] = [];
  search: string = '';
  loading = true;

  constructor(private http: HttpClient) {
    this.fetchScores();
  }

  fetchScores() {
    this.loading = true;
    this.http.get<Score[]>(`http://localhost:8000/index`).subscribe(scores => {
      // En caso que no venga posicion, la asignamos según índice
      this.scores = scores.map((s, i) => ({
        ...s,
        posicion: s.posicion ?? i + 1,
      }));
      this.filteredScores = this.scores;
      this.loading = false;
    });
  }

  onSearch() {
    const term = this.search.trim().toLowerCase();
    if (!term) {
      this.filteredScores = this.scores;
    } else {
      this.filteredScores = this.scores.filter(s => s.user_name?.toLowerCase().includes(term));
    }
  }
}

<div class="puntaje-bg">
</div>
<div class="puntaje-container">
    <h2>Tabla de Puntajes</h2>
    <input type="text" placeholder="Buscar por nombre de usuario..." [(ngModel)]="search" (input)="onSearch()" class="search-bar" />
    <div class="score-table-wrapper">
        <table class="score-table">
            <thead>
                <tr>
                    <th>#</th>
                    <th>Usuario</th>
                    <th>Puntaje</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let score of filteredScores">
                    <td>{{ score.posicion }}</td>
                    <td>{{ score.user_name }}</td>
                    <td>{{ score.score }}</td>
                </tr>
            </tbody>
        </table>
        <div *ngIf="loading" class="loading">Cargando...</div>
        <div *ngIf="!loading && filteredScores.length === 0" class="no-results">No se encontraron resultados.</div>
    </div>
</div>
