import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterOutlet, RouterLink, RouterLinkActive } from '@angular/router'; // RouterLink y RouterLinkActive para la navegación
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-root',
  standalone: true,
  // Si BoardComponent se carga a través del router-outlet, no necesitas importarlo aquí.
  // Solo necesitas RouterOutlet y CommonModule (y RouterLink/RouterLinkActive si los usas en esta plantilla).
  imports: [CommonModule, RouterOutlet, RouterLink, RouterLinkActive],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'Sopa de Letras Dinámica';
  userId: number|null = null;
  userName: string|null = null;
  showLogin = false;
  loginEmail = '';
  loginPassword = '';
  loginError = '';

  constructor(private http: HttpClient) {
    const id = localStorage.getItem('user_id');
    this.userId = id ? +id : null;
    this.userName = localStorage.getItem('user_name');
  }

  openLogin() {
    this.showLogin = true;
    this.loginError = '';
  }

  closeLogin() {
    this.showLogin = false;
    this.loginEmail = '';
    this.loginPassword = '';
    this.loginError = '';
  }

  login() {
    this.loginError = '';
    this.http.post<any>('/api/login', {
      email: this.loginEmail,
      password: this.loginPassword
    }).subscribe({
      next: (res) => {
        if (res && res.id) {
          this.userId = res.id;
          this.userName = res.name || '';
          localStorage.setItem('user_id', String(res.id));
          if (res.name) localStorage.setItem('user_name', res.name);
          this.closeLogin();
        } else {
          this.loginError = 'Credenciales incorrectas';
        }
      },
      error: () => {
        this.loginError = 'Credenciales incorrectas';
      }
    });
  }

  logout() {
    this.userId = null;
    this.userName = null;
    localStorage.removeItem('user_id');
    localStorage.removeItem('user_name');
  }
}
<nav class="main-nav">
  <div class="nav-brand">{{ title }}</div>
  <ul>
    <li *ngIf="!userId">
      <button class="login-btn" (click)="openLogin()">Login</button>
    </li>
    <li *ngIf="userId">
      <span class="user-avatar" title="{{ userName || 'Usuario' }}">
        <svg width="28" height="28" viewBox="0 0 24 24" fill="none" stroke="#3e5227" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><circle cx="12" cy="8" r="4"/><path d="M2 20c0-4 8-6 10-6s10 2 10 6v2H2v-2z"/></svg>
      </span>
      <button class="logout-btn" (click)="logout()" title="Cerrar sesión">Salir</button>
    </li>
  </ul>
</nav>

<div class="login-modal" *ngIf="showLogin">
  <div class="login-content">
    <h3>Iniciar Sesión</h3>
    <input type="email" placeholder="Email" [(ngModel)]="loginEmail" />
    <input type="password" placeholder="Contraseña" [(ngModel)]="loginPassword" />
    <button (click)="login()">Entrar</button>
    <button class="close-btn" (click)="closeLogin()">Cancelar</button>
    <div class="login-error" *ngIf="loginError">{{ loginError }}</div>
  </div>
</div>

<div class="container mt-3">
  <router-outlet></router-outlet>
</div>
