    this.isGameOver = false;
    this.showWinMenu = false;
    this.finalScore = 0;
    this.steps = 0;
    this.unlockKeyboard();
    this.startTimer(); // Iniciar temporizador después de resetear
  }

  resetTimer() {
    switch (this.gameDifficulty) {
      case 'facil':
        this.timeLeft = 240;
        break;
      case 'medio':
        this.timeLeft = 180;
        break;
      case 'dificil':
        this.timeLeft = 120;
        break;
      default:
        this.timeLeft = 240;
