    let attempts = 0;
    const maxAttempts = rows * cols; // Evitar bucle infinito si el tablero está lleno
    do {
      r = Math.floor(Math.random() * rows);
      c = Math.floor(Math.random() * cols);
      attempts++;
      if (attempts > maxAttempts) {
