[_ngcontent-%COMP%] {\n  background: #e74c3c;\n  color: #fff;\n}\n\n.move-left[_ngcontent-%COMP%] {\n  background: #f1c40f;\n  color: #fff;\n}\n\n.move-right[_ngcontent-%COMP%] {\n  background: #2ecc71;\n  color: #fff;\n}\n\n.move-placeholder[_ngcontent-%COMP%] {\n  color: #aaa;\n  font-style: italic;\n  font-size: 0.98em;\n}\n\n.clear-moves[_ngcontent-%COMP%] {\n  background: #dc3545;\n  color: #fff;\n  border: none;\n  border-radius: 50%;\n  width: 28px;\n  height: 28px;\n  font-size: 1.1em;\n  margin-left: 8px;\n  cursor: pointer;\n  transition: background 0.2s, transform 0.1s;\n}\n\n.clear-moves[_ngcontent-%COMP%]:hover {\n  background: #b71c1c;\n  transform: scale(1.1);\n}\n\n.send-moves[_ngcontent-%COMP%] {\n  margin-top: 4px;\n  padding: 10px 22px;\n  background: linear-gradient(90deg, #27ae60 60%, #16a085 100%);\n  color: #fff;\n  border: none;\n  border-radius: 8px;\n  font-size: 1.1em;\n  font-weight: 600;\n  box-shadow: 0 2px 8px rgba(39, 174, 96, 0.08);\n  cursor: pointer;\n  transition: background 0.2s, transform 0.1s;\n}\n\n.send-moves[_ngcontent-%COMP%]:disabled {\n  background: #bdbdbd;\n  color: #fff;\n  cursor: not-allowed;\n}\n\n.send-moves[_ngcontent-%COMP%]:hover:not(:disabled) {\n  background: linear-gradient(90deg, #16a085 60%, #27ae60 100%);\n  transform: scale(1.04);\n}\n\n.steps-counter[_ngcontent-%COMP%] {\n  margin-top: 2px;\n  font-size: 1em;\n  color: #2c3e50;\n  font-weight: 500;\n}\n\n@media (max-width: 768px) {\n  .game-container[_ngcontent-%COMP%] {\n    padding: 10px;\n    margin: 5px;\n  }\n  h2[_ngcontent-%COMP%] {\n    font-size: 18px;\n  }\n  .controls[_ngcontent-%COMP%] {\n    max-width: 200px;\n    gap: 10px;\n  }\n  .controls[_ngcontent-%COMP%]   button[_ngcontent-%COMP%] {\n    width: 70px;\n    height: 70px;\n    font-size: 32px;\n  }\n  .mobile-layout[_ngcontent-%COMP%]   .questions-container[_ngcontent-%COMP%] {\n    flex-direction: column;\n  } \n\n  .mobile-layout[_ngcontent-%COMP%]   .question-item[_ngcontent-%COMP%] 