import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { HttpClient, HttpClientModule } from '@angular/common/http';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-register',
  standalone: true,
  imports: [CommonModule, HttpClientModule, FormsModule],
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent {
  name = '';
  email = '';
  password = '';
  error = '';
  loading = false;
  success = false;

  constructor(private http: HttpClient) {}

  register() {
    this.error = '';
    this.success = false;
    this.loading = true;
    this.http.post<any>('/api/register', {
      name: this.name,
      email: this.email,
      password: this.password
    }).subscribe({
      next: (res) => {
        this.success = true;
        this.loading = false;
      },
      error: (err) => {
        this.error = err.error?.message || 'Error al registrar';
        this.loading = false;
      }
    });
  }
}
<p>register works!</p>
<div class="register-bg"></div>
<div class="register-container">
  <h2>Registro</h2>
  <form (ngSubmit)="register()" autocomplete="off">
    <input type="text" placeholder="Nombre" [(ngModel)]="name" name="name" required />
    <input type="email" placeholder="Email" [(ngModel)]="email" name="email" required />
    <input type="password" placeholder="Contraseña" [(ngModel)]="password" name="password" required />
    <button type="submit" [disabled]="loading">Registrarse</button>
    <div class="register-error" *ngIf="error">{{ error }}</div>
    <div class="register-success" *ngIf="success">¡Registro exitoso! Ahora puedes iniciar sesión.</div>
  </form>
</div>
